// Input có thể là mảng phẳng ([]) hoặc bọc ở { data: [] } / [{ data: [] }]
const raw = $json;
let rows = [];

if (Array.isArray(raw)) {
  rows = raw;
} else if (Array.isArray(raw?.data)) {
  rows = raw.data;
} else if (raw && typeof raw === 'object') {
  rows = [raw];
} else {
  rows = [];
}

return rows
  .map((json) => {
    const codeRaw = (json.ma ?? '').toString().trim();
    const nameRaw = (json.ten ?? '').toString().trim();

    // parent: yêu cầu chỉ lấy khi có parent (không null/rỗng)
    const parentRaw = (json.parentid ?? '').toString().trim();
    const parent = parentRaw ? parentRaw.toUpperCase() : null;

    // Thiếu mã, tên hoặc không có parent -> bỏ qua
    if (!codeRaw || !nameRaw || !parent) return null;

    // Chuẩn hoá mã → UPPERCASE
    const code = codeRaw.toUpperCase();

    // Payload Distribution (có parent_code)
    const payload = {
      doctype: 'Distribution',
      distribution_code: code,
      distribution_name: nameRaw,
      is_active: json.trang_thai !== false,
      status: json.trang_thai !== false ? 'Active' : 'Disabled',
      parent_code: parent,                // <--- bổ sung parent
      mbw_stt: json.stt ?? null,
      mbw_updated_at: json.ngay_cap_nhat ?? null,
    };

    return {
      json: {
        payload_str: JSON.stringify(payload),
        reference_doctype: 'Distribution',
        reference_name: code,
        operation: 'Create', // hoặc 'Upsert' nếu hỗ trợ
        retry_count: 0,
        status: 'Pending',
        log_message: 'Auto-created for Distribution operation',
        create_date: new Date().toISOString(),
        sync_time: null,
        source_data: 'MBW NEXT',
      }
    };
  })
  .filter(Boolean);
