// Map loai_don_hang sang reference_doctype
const mapLoaiDonHang = {
  0: "Sale Order Return",
  1: "Sale Order",
  2: "Sale Invoice"
};

return items.map(item => {
  const data = item.json.body.data[0];

  // Thêm field reference_doctype
  return {
    json: {
      ...item.json,
      body: {
        ...item.json.body,
        data: [
          {
            ...data,
            reference_doctype: mapLoaiDonHang[data.loai_don_hang] || "Sale Order"
          }
        ]
      }
    }
  };
});

// Lấy danh sách khách hàng từ Webhook
const customers = items[0].json.body?.data || [];

// Map dữ liệu
return customers.map(customer => {

  // === 1. Chuẩn hóa dữ liệu cơ bản ===
  const customerCode = (customer.makh || "").trim();
  const customerName = (customer.tenkh || "").trim();

  const groupPath = (customer.nhom_kh || "").trim();
  const status = (customer.trang_thai_kh || "").toLowerCase() === "hoạt động" ? "Active" : "Disabled";
  const creditLimit = Number(customer.han_muc_cn || 0);

  // === 2. Thông tin liên hệ (Contact) ===
  const contact = {
    first_name: (customer.nguoi_lien_he || "").trim(),
    designation: (customer.chuc_vu || "").trim(),
    phone: (customer.sdt || "").trim(),
    email_id: (customer.email || "").trim(),
    birthday: customer.sinh_nhat && customer.sinh_nhat !== "1970-01-01T00:00:00.000Z"
      ? customer.sinh_nhat
      : null
  };

  // === 3. Địa chỉ (Address) ===
  const address = {
    // Địa chỉ chính của khách hàng
    address_primary: (customer.dia_chi || "").trim(),
    // Địa chỉ giao hàng
    address_Shipping: (customer.dc_giao_hangnh || "").trim(),
    // Địa chỉ xuất hóa đơn
    billing_address: (customer.dia_chi_xuat_hoa_don || "").trim(),
    city: "",
    country: "Vietnam"
  };

  // === 4. Payload đầy đủ cho ERPNext ===
  const payload = {
    doctype: "Customer",

    // Thông tin chính
    customer_code: customerCode,
    customer_name: customerName,
    customer_group_path: groupPath,
    customer_type: (customer.loai_kh || "").trim(),
    status: status,
    credit_limit: creditLimit,
    custom_channel: (customer.kenh || "").trim(),

    // **Thông tin xuất hóa đơn & phân loại**
    ma_khach_hang_xuat_hoa_don: (customer.ma_khach_hang_xuat_hoa_don || "").trim(),
    ho_va_ten_khach_hang: (customer.ho_va_ten_khach_hang || "").trim(),
    phan_loai_khach_hang_ma: (customer.phan_loai_khach_hang_ma || "").trim(),
    phan_loai_khach_hang: (customer.phan_loai_khach_hang || "").trim(),
    dia_chi_xuat_hoa_don: (customer.dia_chi_xuat_hoa_don || "").trim(),
    mo_ta_kmxuat_hoa_don_ma: (customer.mo_ta_kmxuat_hoa_don_ma || "").trim(),
    mo_ta_kmxuat_hoa_don: (customer.mo_ta_kmxuat_hoa_don || "").trim(),

    // Địa chỉ và liên hệ
    address,
    contact,

    // Metadata phục vụ kiểm tra
    metadata: {
      created_by: (customer.nguoi_tao || "").trim(),
      updated_by: (customer.nguoi_sua || "").trim(),
      last_updated_at: customer.ngay_cap_nhat || new Date().toISOString()
    }
  };

  // === 5. Trả về dữ liệu đầy đủ ===
  return {
    json: {


      // Thêm thông tin chuẩn hóa cho ERPNext
      payload_str: JSON.stringify(payload),
      reference_doctype: "Customer",
      reference_name: customerCode,
      operation: "Create",
      retry_count: 0,
      status: "Pending",
      log_message: "Auto-created for Customer operation",
      create_date: new Date().toISOString(),
      sync_time: null,
      source_data: "MBW DMS"
    }
  };
});

// Lấy dữ liệu từ Webhook: body.data là array sản phẩm
const inputItems = $input.all()[0].json.body?.data || [];

// Map dữ liệu thành format chuẩn
return inputItems.map(product => {
  // Chuẩn hóa nhóm hàng -> mảng
  const groupPath = (product.nganh_hang || '')
    .split('>')
    .map(s => s.trim())
    .filter(Boolean);

  // VAT %
  const vat = Number(product.vat || 0);

  // Hệ số quy đổi (dvt_chan -> dvt_le)
  const factor = Number(product.hsqd || product.hsgd || product.hsg || 1);

  // Build payload
  const payload = {
    doctype: "Item",
    item_code: (product.ma_sp || "").trim(),
    item_name: (product.ten_sp || "").trim(),
    brand: (product.nhan_hieu || "").trim(),
    item_group_path: groupPath,
    default_supplier: (product.nha_cung_cap || "").trim(),
    stock_uom: (product.dvt_le || "Nos").trim(),
    description: product.mo_ta || "",
    is_stock_item: 1,
    disabled: 0,

    // Thông tin xuất hóa đơn
    custom_invoice_item_name: product.ten_xuat_hoa_don || "",
    custom_invoice_item_code: product.ma_xuat_hoa_don || "",

    // Quy đổi đơn vị
    uom_conversions: {
      big_uom: product.dvt_chan || "",
      small_uom: product.dvt_le || "",
      factor
    },

    // Mã vạch
    barcodes: product.ma_vach ? [{
      barcode: String(product.ma_vach),
      barcode_type: String(product.ma_vach).length === 13 ? "EAN-13" : "Code128"
    }] : [],

    // Giá bán & mua
    prices: [
      { price_list: "Standard Buying",    price_list_rate: Number(product.gia_nhap || product.gia_nhap_le || 0) },
      { price_list: "Retail Selling",     price_list_rate: Number(product.gia_le || 0) },
      { price_list: "Wholesale Selling",  price_list_rate: Number(product.gia_chan || 0) }
    ].filter(p => p.price_list_rate > 0),

    // Thuế
    tax: { vat_percent: vat },

    // Hình ảnh
    images: Array.isArray(product.hinh_anh) ? product.hinh_anh.filter(Boolean) : []
  };

  return {
    json: {
      payload_str: JSON.stringify(payload), // ✅ Bắt buộc stringify
      reference_doctype: "Item",
      reference_name: payload.item_code,
      operation: "Create",
      retry_count: 0,
      status: "Pending",
      log_message: "Auto-created for Item operation",
      create_date: new Date().toISOString(),
      sync_time: null,
      source_data: "MBW DMS"
    }
  };
});
