// Biến một mảng trong 1 item thành nhiều item riêng
function safeParse(value) {
  if (typeof value !== 'string') return value;
  const s = value?.trim?.() ?? '';
  if (!s || (s[0] !== '{' && s[0] !== '[')) return value;
  try { return JSON.parse(s); } catch { return value; }
}

const all = $items("MBWD ERPNext");
let records;

// 1) Trường hợp API trả về mảng ngay ở root
if (Array.isArray(all[0]?.json)) {
  records = all[0].json;

// 2) Hoặc gói trong thuộc tính data
} else if (Array.isArray(all[0]?.json?.data)) {
  records = all[0].json.data;

// 3) Hoặc đã là nhiều item sẵn
} else {
  records = all.map(i => i.json);
}

// Nếu mỗi record có trường payload dạng string → parse sang object (không bắt buộc)
return records.map(r => {
  const parsed = ('payload' in r) ? safeParse(r.payload) : r.payload;
  return { json: { ...r, payload: parsed } };
});

// Code Node sau "Merge Data Info"

// Helper chuẩn hóa chuỗi để so sánh
const N = s => (s ?? '').toString().trim().toUpperCase();

// Lấy dữ liệu ERP đã split
const erpItems = $items().map(i => i.json);
console.log(erpItems)

// Lấy dữ liệu từ DMS
const dmsItems = $items("Get Khách Hàng in MobiworkDMS").map(i => i.json);

// Tạo set cho nhanh
const dmsSet = new Set(dmsItems.map(x => N(x.makh)));

// Gắn cờ exists
return erpItems.map(r => {
  const key = N(r.reference_name);
  return {
    json: {
      ...r,
      exists: dmsSet.has(key),
      existed_name: dmsSet.has(key) ? key : null
    }
  };
});

// ===== Lấy dữ liệu từ node "Loop Over Items" =====
const cur = $('Loop Over Items').item.json;

// Payload chính của khách hàng
const p = cur.payload || {};

// --------------------
// 1. Chuẩn hoá nhóm khách hàng
// --------------------
let customerGroup = "";
let parentCustomerGroup = "";

if (p.customer_group_path) {
  const groupParts = p.customer_group_path
    .split('>')
    .map(s => s.trim())
    .filter(Boolean);

  if (groupParts.length > 0) {
    customerGroup = groupParts[groupParts.length - 1]; // phần tử cuối cùng
    if (groupParts.length > 1) {
      parentCustomerGroup = groupParts[groupParts.length - 2]; // phần tử áp chót
    }
  }
}

// --------------------
// 2. Chuẩn hoá contact
// --------------------
const contact = p.contact || {};
const phone = (contact.phone || "").trim();
const email = (contact.email_id || "").trim();

// --------------------
// 3. Chuẩn hoá address (Primary, Billing, Shipping)
// --------------------
const address = p.address || {};
const customerName = (p.customer_name || "").trim();
const customerCode = (p.customer_code || "").trim();

// Hàm tạo address_title theo quy tắc
function buildAddressTitle(prefix) {
  return `${prefix} - ${customerName} - ${customerCode}`;
}

// Hàm tách city từ address_line1 (lấy phần tử cuối cùng sau dấu ",")
function extractCity(line1) {
  if (!line1) return "";
  const parts = line1.split(',').map(s => s.trim()).filter(Boolean);
  return parts.length > 0 ? parts[parts.length - 1] : "";
}

// Primary Address
const primaryLine = (address.address_primary || "").trim();
const primaryAddress = {
  address_title: buildAddressTitle("Địa chỉ chính"),
  address_type: "Office",
  address_line1: primaryLine,
  city: extractCity(primaryLine),
  country: (address.country || "Vietnam").trim()
};

// Billing Address
const billingLine = (address.billing_address || "").trim();
const billingAddress = {
  address_title: buildAddressTitle("Địa chỉ thanh toán"),
  address_type: "Billing",
  address_line1: billingLine,
  city: extractCity(billingLine),
  country: (address.country || "Vietnam").trim()
};

// Shipping Address
const shippingLine = (address.address_Shipping || "").trim();
const shippingAddress = {
  address_title: buildAddressTitle("Địa chỉ giao hàng"),
  address_type: "Shipping",
  address_line1: shippingLine,
  city: extractCity(shippingLine),
  country: (address.country || "Vietnam").trim()
};

// --------------------
// 4. Chuẩn hoá metadata
// --------------------
const metadata = p.metadata || {};

// --------------------
// 5. Chuẩn hoá Customer
// --------------------
const customer = {
  reference_doctype: cur.reference_doctype || "",
  reference_name: cur.reference_name || "",
  operation: cur.operation || "",
  name: cur.name || "",

  // Thông tin cơ bản
  customer_code: customerCode,
  customer_name: customerName,
  customer_group: customerGroup,
  parent_customer_group: parentCustomerGroup || "",
  customer_type: (p.customer_type || "Individual").trim(),
  status: p.status || "Active",
  credit_limit: Number(p.credit_limit || 0),
  custom_channel: (p.custom_channel || "").trim(),

  // Thông tin đặc biệt
  ma_khach_hang_xuat_hoa_don: (p.ma_khach_hang_xuat_hoa_don || "").trim(),
  ho_va_ten_khach_hang: (p.ho_va_ten_khach_hang || "").trim(),
  phan_loai_khach_hang_ma: (p.phan_loai_khach_hang_ma || "").trim(),
  phan_loai_khach_hang: (p.phan_loai_khach_hang || "").trim(),
  dia_chi_xuat_hoa_don: (p.dia_chi_xuat_hoa_don || "").trim(),
  mo_ta_kmxuat_hoa_don_ma: (p.mo_ta_kmxuat_hoa_don_ma || "").trim(),
  mo_ta_kmxuat_hoa_don: (p.mo_ta_kmxuat_hoa_don || "").trim(),

  // Liên hệ
  phone: phone,
  email: email,
  contact_first_name: (contact.first_name || "").trim(),
  contact_designation: (contact.designation || "").trim(),
  contact_birthday: contact.birthday || null,

  // Metadata
  created_by: (metadata.created_by || "").trim(),
  updated_by: (metadata.updated_by || "").trim(),
  last_updated_at: metadata.last_updated_at || null
};

// --------------------
// 6. Gộp dữ liệu thành Output chuẩn
// --------------------
const out = {
  customer,
  address: [primaryAddress, billingAddress, shippingAddress]
};

return { json: out };

