// Biến một mảng trong 1 item thành nhiều item riêng
function safeParse(value) {
  if (typeof value !== 'string') return value;
  const s = value?.trim?.() ?? '';
  if (!s || (s[0] !== '{' && s[0] !== '[')) return value;
  try { return JSON.parse(s); } catch { return value; }
}

const all =  $items("MBWD ERPNext", 0, 0);
let records;

// 1) Trường hợp API trả về mảng ngay ở root
if (Array.isArray(all[0]?.json)) {
  records = all[0].json;

// 2) Hoặc gói trong thuộc tính data
} else if (Array.isArray(all[0]?.json?.data)) {
  records = all[0].json.data;

// 3) Hoặc đã là nhiều item sẵn
} else {
  records = all.map(i => i.json);
}

// Nếu mỗi record có trường payload dạng string → parse sang object (không bắt buộc)
return records.map(r => {
  const parsed = ('payload' in r) ? safeParse(r.payload) : r.payload;
  return { json: { ...r, payload: parsed } };
});

const arr = $items('Lấy đơn hàng trong ERPNext'); 

// Kiểm tra xem item đầu tiên có field name không
const first = arr[0]?.json || {};

return [{
  json: {
    exists: !!first.name,   // true nếu có giá trị name
    existed_name: first.name || null,
    data: arr[0]?.json || null,
  }
}];

// ===== Lấy dữ liệu từ node "Loop Over Items" =====
const cur = $('Loop Over Items').item.json;

// Payload chính của khách hàng
const p = cur.payload || {};

// --------------------
// 1. Chuẩn hoá nhóm khách hàng
// --------------------
let customerGroup = "";
let parentCustomerGroup = "";

if (p.customer_group_path) {
  const groupParts = p.customer_group_path
    .split('>')
    .map(s => s.trim())
    .filter(Boolean);

  if (groupParts.length > 0) {
    customerGroup = groupParts[groupParts.length - 1]; // phần tử cuối cùng
    if (groupParts.length > 1) {
      parentCustomerGroup = groupParts[groupParts.length - 2]; // phần tử áp chót
    }
  }
}

// --------------------
// 2. Chuẩn hoá contact
// --------------------
const contact = p.contact || {};
const phone = (contact.phone || "").trim();
const email = (contact.email_id || "").trim();

// --------------------
// 3. Chuẩn hoá address (Primary, Billing, Shipping)
// --------------------
const address = p.address || {};
const customerName = (p.customer_name || "").trim();
const customerCode = (p.customer_code || "").trim();

// Hàm tạo address_title theo quy tắc
function buildAddressTitle(prefix) {
  return `${prefix} - ${customerName} - ${customerCode}`;
}

// Hàm tách city từ address_line1 (lấy phần tử cuối cùng sau dấu ",")
function extractCity(line1) {
  if (!line1) return "";
  const parts = line1.split(',').map(s => s.trim()).filter(Boolean);
  return parts.length > 0 ? parts[parts.length - 1] : "";
}

// Primary Address
const primaryLine = (address.address_primary || "").trim();
const primaryAddress = {
  address_title: buildAddressTitle("Địa chỉ chính"),
  address_type: "Office",
  address_line1: primaryLine,
  city: extractCity(primaryLine),
  country: (address.country || "Vietnam").trim()
};

// Billing Address
const billingLine = (address.billing_address || "").trim();
const billingAddress = {
  address_title: buildAddressTitle("Địa chỉ thanh toán"),
  address_type: "Billing",
  address_line1: billingLine,
  city: extractCity(billingLine),
  country: (address.country || "Vietnam").trim()
};

// Shipping Address
const shippingLine = (address.address_Shipping || "").trim();
const shippingAddress = {
  address_title: buildAddressTitle("Địa chỉ giao hàng"),
  address_type: "Shipping",
  address_line1: shippingLine,
  city: extractCity(shippingLine),
  country: (address.country || "Vietnam").trim()
};

// --------------------
// 4. Chuẩn hoá metadata
// --------------------
const metadata = p.metadata || {};

// --------------------
// 5. Chuẩn hoá Customer
// --------------------
const customer = {
  reference_doctype: cur.reference_doctype || "",
  reference_name: cur.reference_name || "",
  operation: cur.operation || "",
  name: cur.name || "",

  // Thông tin cơ bản
  customer_code: customerCode,
  customer_name: customerName,
  customer_group: customerGroup,
  parent_customer_group: parentCustomerGroup || "",
  customer_type: (p.customer_type || "Individual").trim(),
  status: p.status || "Active",
  credit_limit: Number(p.credit_limit || 0),
  custom_channel: (p.custom_channel || "").trim(),

  // Thông tin đặc biệt
  ma_khach_hang_xuat_hoa_don: (p.ma_khach_hang_xuat_hoa_don || "").trim(),
  ho_va_ten_khach_hang: (p.ho_va_ten_khach_hang || "").trim(),
  phan_loai_khach_hang_ma: (p.phan_loai_khach_hang_ma || "").trim(),
  phan_loai_khach_hang: (p.phan_loai_khach_hang || "").trim(),
  dia_chi_xuat_hoa_don: (p.dia_chi_xuat_hoa_don || "").trim(),
  mo_ta_kmxuat_hoa_don_ma: (p.mo_ta_kmxuat_hoa_don_ma || "").trim(),
  mo_ta_kmxuat_hoa_don: (p.mo_ta_kmxuat_hoa_don || "").trim(),

  // Liên hệ
  phone: phone,
  email: email,
  contact_first_name: (contact.first_name || "").trim(),
  contact_designation: (contact.designation || "").trim(),
  contact_birthday: contact.birthday || null,

  // Metadata
  created_by: (metadata.created_by || "").trim(),
  updated_by: (metadata.updated_by || "").trim(),
  last_updated_at: metadata.last_updated_at || null
};

// --------------------
// 6. Gộp dữ liệu thành Output chuẩn
// --------------------
const out = {
  customer,
  address: [primaryAddress, billingAddress, shippingAddress]
};

return { json: out };

// ===== Lấy dữ liệu từ node "Loop Over Items" =====
const cur = $('Loop Over Items').item.json;
const p = cur.payload || {};

// ===== Lấy map kho từ node "ERPNext Warehouses" =====
const warehouses = $('Lấy danh sách kho hàng').item.json;

// Hàm tìm kho theo custom_warehouse_code
function findWarehouseName(code) {
  if (!code) return null;
  const w = warehouses.find(w => w.custom_warehouse_code === code);
  return w ? w.name : null;
}

// --------------------
// Hàm format ngày về "YYYY-MM-DD"
// --------------------
function formatDate(dateStr) {
  if (!dateStr) return null;
  try {
    const d = new Date(dateStr);
    if (isNaN(d.getTime())) return null;
    return d.toISOString().split('T')[0];
  } catch {
    return null;
  }
}

// --------------------
// 1. Chuẩn hoá danh sách sản phẩm
// --------------------
const mainProducts = Array.isArray(p.san_pham) ? p.san_pham : [];
const promoProducts = Array.isArray(p.san_pham_km) ? p.san_pham_km : [];
promoProducts.forEach(sp => sp.is_km = true);
const products = [...mainProducts, ...promoProducts];

const allProducts = products.map(sp => ({
  item_code: (sp.ma_sp || sp.ma_sp_km || "").trim(),
  item_name: (sp.ten_sp || sp.ten_sp_km || "").trim(),
  warehouse: (sp.ma_kho_xuat || sp.ma_kho_xuat_km || "").trim(),
  qty: Number(sp.so_luong || sp.so_luong_km || 0),
  rate: sp.is_km ? 0 : Number(sp.don_gia || 0),
  amount: sp.is_km ? 0 : Number(sp.thanh_tien || 0),
  discount_amount: Number(sp.chiet_khau || 0),
  discount_npp: Number(sp.chiet_khau_npp || 0),
  vat_rate: Number(sp.vat_sp || 0),
  note: (sp.ghi_chu || sp.ghi_chu_km || "").trim(),
  is_promotion: !!sp.is_km,
  promotion_code: (sp.ctkm || "").trim()
}));

// --------------------
// 2. Chuẩn hoá thông tin đơn hàng
// --------------------
const saleOrder = {
  reference_doctype: cur.reference_doctype || "Sale Order",
  reference_name: cur.reference_name || "",
  operation: cur.operation || "Create",
  name: cur.name || "",
  order_code: (p.ma_phieu || "").trim(),
  customer_code: (p.ma_kh || "").trim(),
  customer_name: (p.ten_kh || "").trim(),
  phone: (p.sdt || "").trim(),
  address: (p.dia_chi || "").trim(),

  order_status: "To Deliver and Bill",
  order_date: formatDate(p.ngay_dat),

  total_amount: Number(p.tong_tien_hang || 0),
  total_tax: Number(p.tong_tien_vat || 0),
  order_discount: Number(p.ck_don_hang || 0),
  grand_total: Number(p.phai_thanh_toan || 0),

  // 💰 Debit (thanh toán trước)
  debit: Number(p.thanh_toan_truoc || p.debit || p.tra_truoc || 0),

  customer_group_code: (p.ma_nhom || "").trim(),
  customer_group_name: (p.ten_nhom || "").trim(),

  order_by: (p.ten_nguoi_dat || "").trim(),
  approved_by: (p.nguoi_duyet || "").trim(),
  approval_date: formatDate(p.ngay_duyet),

  created_by: (p.nguoi_tao || "").trim(),
  created_at: formatDate(p.ngay_tao),
  updated_by: (p.nguoi_sua || "").trim(),
  updated_at: formatDate(p.ngay_sua),

  invoice_address: (p.dia_chi_xuat_hoa_don || "").trim(),
  invoice_code: (p.ma_xuat_hoa_don || "").trim(),
  tax_code: (p.ma_so_thue || "").trim(),
  cccd: (p.cccd || "").trim(),
  thue15: Number(p.thue15 || 0),
  invoice_name: (p.ten_xuat_hoa_don || "").trim()
};

// --------------------
// 3. Chuẩn hoá custom_promotion từ p.promotion
// --------------------
const promotions = [];
if (Array.isArray(p.promotion)) {
  for (const km of p.promotion) {
    for (const pr of km.product || []) {
      promotions.push({
        promotion_code: km.id || "",
        promotion_name: km.ten_khuyen_mai || "",
        promotion_type: km.ptype?.value || "",
        item_code: pr.ma_san_pham || "",
        item_name: pr.ten_san_pham || "",
        promo_qty: Number(pr.so_luong) || 0,
        uom: pr.don_vi_tinh?.viewData || "",
        discount_amount: 0
      });
    }
  }
}

// --------------------
// 5. Ghép lại payload cho ERPNext
// --------------------
const erpNextPayload = {
  docstatus: 1,
  doctype: "Sales Order",
  naming_series: "SO-.YYYY.-",
  custom_sales_person:
    ($input.first()?.json?.['0']?.employee_name || "") + "_" +
    ($input.first()?.json?.['0']?.employee || ""),
  transaction_date: saleOrder.order_date,
  delivery_date: saleOrder.order_date,

  order_type: "Sales",
  status: saleOrder.order_status,

  ignore_pricing_rule: true,
  customer: saleOrder.customer_code,
  custom_customer_code: saleOrder.customer_code,
  custom_sales_order_dms_id: saleOrder.order_code,
  custom_customer_group_code: saleOrder.customer_group_code,
  custom_customer_group_name: saleOrder.customer_group_name,

  contact_mobile: saleOrder.phone,
  contact_address: saleOrder.address,

  custom_order_by: saleOrder.order_by,
  custom_approved_by: saleOrder.approved_by,
  custom_approval_date: saleOrder.approval_date,

  custom_created_by: saleOrder.created_by,
  custom_created_at: saleOrder.created_at,
  custom_updated_by: saleOrder.updated_by,
  custom_updated_at: saleOrder.updated_at,

  custom_invoice_address: saleOrder.invoice_address,
  custom_customer_billing_code: saleOrder.invoice_code,
  custom_tax_code: saleOrder.tax_code,
  custom_personal_id_number: saleOrder.cccd,
  custom_customer_billing_name: saleOrder.invoice_name,

  total: saleOrder.total_amount,
  total_taxes_and_charges: saleOrder.total_tax,
  discount_amount: saleOrder.order_discount,
  grand_total: saleOrder.grand_total,

  // 💵 Mapping thêm field debit
  custom_debit: saleOrder.debit,

  // ✅ Danh sách sản phẩm
  items: allProducts.map(item => {
    const base = {
      item_code: item.item_code,
      item_name: item.item_name,
      warehouse: findWarehouseName(item.warehouse),
      qty: item.qty,
      rate: item.rate,
      amount: item.amount,
      discount_amount: item.discount_amount,
      is_free_item: item.is_promotion ? 1 : 0,
      custom_promotion_code: item.promotion_code,
      description: item.note
    };
    if (item.is_promotion) base.price_list_rate = 0;
    return base;
  }),

  custom_promotion: promotions
};

// --------------------
// 6. Trả dữ liệu ra ngoài
// --------------------
return { json: erpNextPayload };

const arr = $items('Check DMS Customer Type'); 

// Kiểm tra xem item đầu tiên có field name không
const first = arr[0]?.json || {};

return [{
  json: {
    exists: !!first.name,   // true nếu có giá trị name
    existed_name: first.name || null,
    data: arr[0]?.json || null,
  }
}];

const arr = $items('Check Customer Group'); 

// Kiểm tra xem item đầu tiên có field name không
const first = arr[0]?.json || {};

return [{
  json: {
    exists: !!first.name,   // true nếu có giá trị name
    existed_name: first.name || null,
    data: arr[0]?.json || null,
  }
}];

// ===== Lấy dữ liệu từ node "Loop Over Items" =====
const cur = $('[Update] Chuyển đổi dữ liệu').item.json;


// Kiểm tra xem có mảng address không
if (!cur || !Array.isArray(cur.address)) {
  return [];
}

// Trả về từng địa chỉ như một item riêng
return cur.address.map(addr => ({ json: addr }));

// ===== Lấy dữ liệu từ node "Loop Over Items" =====
const cur = $('[Add] Chuyển đổi dữ liệu').item.json;


// Kiểm tra xem có mảng address không
if (!cur || !Array.isArray(cur.address)) {
  return [];
}

// Trả về từng địa chỉ như một item riêng
return cur.address.map(addr => ({ json: addr }));

