// Bi·∫øn m·ªôt m·∫£ng trong 1 item th√†nh nhi·ªÅu item ri√™ng
function safeParse(value) {
  if (typeof value !== 'string') return value;
  const s = value?.trim?.() ?? '';
  if (!s || (s[0] !== '{' && s[0] !== '[')) return value;
  try { return JSON.parse(s); } catch { return value; }
}

const all =  $items("MBWD ERPNext", 0, 0);
let records;

// 1) Tr∆∞·ªùng h·ª£p API tr·∫£ v·ªÅ m·∫£ng ngay ·ªü root
if (Array.isArray(all[0]?.json)) {
  records = all[0].json;

// 2) Ho·∫∑c g√≥i trong thu·ªôc t√≠nh data
} else if (Array.isArray(all[0]?.json?.data)) {
  records = all[0].json.data;

// 3) Ho·∫∑c ƒë√£ l√† nhi·ªÅu item s·∫µn
} else {
  records = all.map(i => i.json);
}

// N·∫øu m·ªói record c√≥ tr∆∞·ªùng payload d·∫°ng string ‚Üí parse sang object (kh√¥ng b·∫Øt bu·ªôc)
return records.map(r => {
  const parsed = ('payload' in r) ? safeParse(r.payload) : r.payload;
  return { json: { ...r, payload: parsed } };
});

const arr = $items('L·∫•y ƒë∆°n h√†ng trong ERPNext'); 

// Ki·ªÉm tra xem item ƒë·∫ßu ti√™n c√≥ field name kh√¥ng
const first = arr[0]?.json || {};

return [{
  json: {
    exists: !!first.name,   // true n·∫øu c√≥ gi√° tr·ªã name
    existed_name: first.name || null,
    data: arr[0]?.json || null,
  }
}];

// ===== L·∫•y d·ªØ li·ªáu t·ª´ node "Loop Over Items" =====
const cur = $('Loop Over Items').item.json;
const p = cur.payload || {};
// ‚úÖ L·∫•y th√¥ng tin tuy·∫øn (n·∫øu c√≥)
const route = p.tuyen?.viewData || "";
const routeId = p.tuyen?.code || "";
// ===== L·∫•y map kho t·ª´ node "ERPNext Warehouses" =====
const warehouses = $('L·∫•y danh s√°ch kho h√†ng').item.json;

// ===== L·∫•y danh s√°ch VAT t·ª´ node "L·∫•y danh s√°ch VAT" =====
const vatList = $('L·∫•y danh s√°ch VAT').item.json || [];

// --------------------
// H√†m t√¨m kho theo custom_warehouse_code
// --------------------
function findWarehouseName(code) {
  if (!code) return null;
  const w = warehouses.find(w => w.custom_warehouse_code === code);
  return w ? w.name : null;
}

// --------------------
// H√†m t√¨m t√™n/ID VAT theo m√£ VAT (ho·∫∑c ph·∫ßn trƒÉm VAT)
// --------------------
function findVatAccount(vatValue) {
  if (!vatValue) return null;
  // v√≠ d·ª•: vatValue = 8 ‚Üí t√¨m trong custom_vat_rate = 8 ho·∫∑c name = "VAT 8%"
  const v = vatList.find(v =>
    v.custom_vat_rate === Number(vatValue) ||
    v.name?.includes(`${vatValue}`) ||
    v.title?.includes(`${vatValue}`)
  );
  return v ? v.name : null;
}

// --------------------
// H√†m format ng√†y v·ªÅ "YYYY-MM-DD" (GMT+7)
// --------------------
function formatDate(dateStr) {
  if (!dateStr) return null;
  try {
    const d = new Date(dateStr);
    if (isNaN(d.getTime())) return null;
    // C·ªông th√™m 7 ti·∫øng (7 * 60 * 60 * 1000 ms)
    const vnTime = new Date(d.getTime() + 7 * 60 * 60 * 1000);
    return vnTime.toISOString().split('T')[0];
  } catch {
    return null;
  }
}

// --------------------
// 1. Chu·∫©n ho√° danh s√°ch s·∫£n ph·∫©m
// --------------------
