  sales_person:
    $('T√¨m ki·∫øm nh√¢n vi√™n sale').first().json['0'].employee_name + "_" +
   $('T√¨m ki·∫øm nh√¢n vi√™n sale').first().json['0'].name ,
  transaction_date: saleOrder.order_date,
