  // ‚úÖ C·ªông t·ªïng chi·∫øt kh·∫•u item v√†o chi·∫øt kh·∫•u ƒë∆°n h√†ng
  discount_amount: saleOrder.order_discount + totalItemDiscount,
  grand_total: saleOrder.grand_total,

