  custom_debit: saleOrder.debit,
// ‚úÖ Th√™m t·ªïng chi·∫øt kh·∫•u s·∫£n ph·∫©m (m·ªõi)
  custom_item_discount_amount: totalItemDiscount,
  custom_order_discount_amount:saleOrder.order_discount,
  // ‚úÖ Danh s√°ch s·∫£n ph·∫©m c√≥ VAT
  items: allProducts.map(item => {
    const base = {
      item_code: item.item_code,
      item_name: item.item_name,
      warehouse: findWarehouseName(item.warehouse),
      qty: item.qty,
