// ==============================
// Lấy danh sách khách hàng từ Webhook
// ==============================
const customers = items[0].json.body || [];

// Kiểm tra dữ liệu hợp lệ
if (!Array.isArray(customers) || customers.length === 0) {
  return [{ json: { error: "Không có dữ liệu khách hàng trong webhook" } }];
}

// ==============================
// Duyệt từng khách hàng và chuẩn hóa dữ liệu
// ==============================
return customers.map(customer => {

  // === 1. Chuẩn hóa dữ liệu cơ bản ===
  const customerCode = (customer.makh || "").trim();
  const customerName = (customer.tenkh || "").trim();
  const groupPath = (customer.nhom_kh || "").trim();

  const statusValue = (customer.trang_thai_kh || "").toLowerCase();
  const status = statusValue === "hoạt động" || statusValue === "active" ? "Active" : "Disabled";

  const creditLimit = Number(customer.han_muc_cn || 0);

  // === 2. Thông tin liên hệ (Contact) ===
  const contact = {
    first_name: (customer.nguoi_lien_he || "").trim(),
    designation: (customer.chuc_vu || "").trim(),
    phone: (customer.sdt || "").trim(),
    email_id: (customer.email || "").trim(),
    birthday:
      customer.sinh_nhat && customer.sinh_nhat !== "1970-01-01T00:00:00.000Z"
        ? customer.sinh_nhat
        : null
  };

  // === 3. Địa chỉ (Address) ===
  const address = {
    address_primary: (customer.dia_chi || "").trim(),
    address_shipping: (customer.dc_giao_hangnh || "").trim(),
    billing_address: (customer.dia_chi_xuat_hoa_don || "").trim(),
    city: "",
    country: "Vietnam"
  };

  // === 4. Payload đầy đủ cho ERPNext ===
  const payload = {
    doctype: "Customer",

    // Thông tin chính
    customer_code: customerCode,
    customer_name: customerName,
    customer_group_path: groupPath,
    customer_type: (customer.loai_kh || "").trim(),
    status: status,
    credit_limit: creditLimit,
    custom_channel: (customer.kenh || "").trim(),

    // Thông tin xuất hóa đơn & phân loại
    ma_khach_hang_xuat_hoa_don: (customer.ma_khach_hang_xuat_hoa_don || "").trim(),
    ho_va_ten_khach_hang: (customer.ho_va_ten_khach_hang || "").trim(),
    phan_loai_khach_hang_ma: (customer.phan_loai_khach_hang_ma || "").trim(),
    phan_loai_khach_hang: (customer.phan_loai_khach_hang || "").trim(),
    dia_chi_xuat_hoa_don: (customer.dia_chi_xuat_hoa_don || "").trim(),
    mo_ta_kmxuat_hoa_don_ma: (customer.mo_ta_kmxuat_hoa_don_ma || "").trim(),
    mo_ta_kmxuat_hoa_don: (customer.mo_ta_kmxuat_hoa_don || "").trim(),

    // Địa chỉ và liên hệ
    address,
    contact,

    // Metadata
    metadata: {
      created_by: (customer.nguoi_tao || "").trim(),
      updated_by: (customer.nguoi_sua || "").trim(),
      last_updated_at: customer.ngay_cap_nhat || new Date().toISOString()
    }
  };

  // === 5. Trả về dữ liệu chuẩn cho ERPNext Queue ===
  return {
    json: {
      payload_str: JSON.stringify(payload),
      reference_doctype: "Customer",
      reference_name: customerCode,
      operation: "Create",
      retry_count: 0,
      status: "Pending",
      log_message: "Auto-created for Customer operation",
      create_date: new Date().toISOString(),
      sync_time: null,
      source_data: "MBW DMS"
    }
  };
});
