// === 1. Lấy tất cả dữ liệu từ node trước ===
// "items" là mảng mặc định n8n truyền vào Code node
let allOrders = [];

// Duyệt qua tất cả items từ node trước
for (const item of items) {
  const data = item.json;

  // Nếu data là array (nhiều đơn hàng), gộp vào allOrders
  if (Array.isArray(data)) {
    allOrders.push(...data);
  } else {
    // Nếu chỉ là 1 object, push trực tiếp
    allOrders.push(data);
  }
}

console.log('Total Orders:', allOrders.length);
console.log('Sample Order:', JSON.stringify(allOrders[0], null, 2));

// Nếu không có đơn hàng, return rỗng
if (allOrders.length === 0) {
  return [];
}

// === 2. Duyệt qua từng đơn hàng và chuẩn hóa dữ liệu ===
return allOrders.map(order => {
  const orderCode = (order.ma_phieu || "").trim();

  // 2.1. Chuẩn hóa sản phẩm
  const products = (order.san_pham || []).map(sp => ({
    stt: sp.stt,
    ma_sp: (sp.ma_sp || "").trim(),
    ten_sp: (sp.ten_sp || "").trim(),
    don_vi_tinh: (sp.ten_dvt || "").trim(),
    kho_xuat: (sp.ma_kho_xuat || "").trim(),
    so_luong: Number(sp.so_luong || 0),
    don_gia: Number(sp.don_gia || 0),
    thanh_tien: Number(sp.thanh_tien || 0),
    chiet_khau: Number(sp.chiet_khau || 0),
    vat: Number(sp.vat || 0),
    ghi_chu: (sp.ghi_chu || "").trim(),
    is_km: sp.is_km === true,
    ctkm: (sp.ctkm || "").trim()
  }));

  // 2.2. Chuẩn hóa sản phẩm khuyến mãi
  const promotions = (order.san_pham_km || []).map(km => ({
    stt: km.stt_km,
    ma_sp: (km.ma_sp_km || "").trim(),
    ten_sp: (km.ten_sp_km || "").trim(),
    don_vi_tinh: (km.ten_dvt_km || "").trim(),
    kho_xuat: (km.ma_kho_xuat_km || "").trim(),
    so_luong: Number(km.so_luong_km || 0),
    is_km: km.is_km === true,
    ctkm: (km.ctkm || "").trim()
  }));

  // 2.3. Tạo payload chuẩn
  const payload = {
    doctype: "Sales Order",
    order_code: orderCode,
    customer_code: (order.ma_kh || "").trim(),
    customer_name: (order.ten_kh || "").trim(),
    address: (order.dia_chi || "").trim(),
    phone: (order.sdt || "").trim(),
    order_date: order.ngay_dat || null,
    group_code: (order.ma_nhom || "").trim(),
    order_status: (order.trang_thai || "").trim(),

    created_by: (order.nguoi_tao || "").trim(),
    updated_by: (order.nguoi_sua || "").trim(),
    created_at: order.ngay_tao || null,
    updated_at: order.ngay_sua || null,

    approved_by: (order.nguoi_duyet || "").trim(),
    approved_at: order.ngay_duyet || null,

    // Tổng tiền
    total_amount: Number(order.tong_tien_hang || 0),
    total_vat: Number(order.tong_tien_vat || 0),
    total_discount_item: Number(order.tong_ck_sp || 0),
    order_discount: Number(order.ck_don_hang || 0),
    grand_total: Number(order.phai_thanh_toan || 0),

    // Danh sách sản phẩm
    products,
    promotions
  };

  // === 3. Trả về format chuẩn cho n8n ===
  return {
    json: {
      payload_str: JSON.stringify(payload),
      reference_doctype: "Sales Order",
      reference_name: orderCode,
      operation: "Create",
      retry_count: 0,
      status: "Pending",
      log_message: "Auto-created for Sales Order operation",
      create_date: new Date().toISOString(),
      sync_time: null,
      source_data: "MBW NEXT"
    }
  };
});
